// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";
import {IBribeMarket} from "./interfaces/IBribeMarket.sol";
import {IBribeVault} from "./interfaces/IBribeVault.sol";
import {Errors} from "./libraries/Errors.sol";

contract BribeFactory is Ownable2Step {
    address public bribeMarketImplementation;
    address public bribeVault;
    uint256 public constant MAX_PERIODS = 10;
    uint256 public constant MAX_PERIOD_DURATION = 30 days;

    event BribeMarketCreated(address indexed bribeMarket);
    event BribeMarketImplementationUpdated(
        address indexed bribeMarketImplementation
    );
    event BribeVaultUpdated(address indexed bribeVault);

    /**
        @notice Check if the specified address is a contract
        @param  _address  Address to be checked
     */
    modifier isContract(address _address) {
        uint256 codeSize;
        assembly {
            codeSize := extcodesize(_address)
        }
        if (codeSize == 0) revert Errors.NotAContract();
        _;
    }

    /**
        @param  _implementation  Address of the implementation contract
        @param  _bribeVault      Address of the bribe vault
     */
    constructor(address _implementation, address _bribeVault) {
        _setBribeMarketImplementation(_implementation);
        _setBribeVault(_bribeVault);
    }

    /**
        @notice Deploy a new bribe market
        @param  _protocol        address  Market name/identifier
        @param  _maxPeriods      uint256  Maximum number of periods for bribe deposits
        @param  _periodDuration  uint256  Period duration in each voting round
     */
    function createBribeMarket(
        string calldata _protocol,
        uint256 _maxPeriods,
        uint256 _periodDuration
    ) external returns (address) {
        if (_maxPeriods == 0 || _maxPeriods > MAX_PERIODS)
            revert Errors.InvalidMaxPeriod();
        if (_periodDuration == 0 || _periodDuration > MAX_PERIOD_DURATION)
            revert Errors.InvalidPeriodDuration();

        address bribeMarket = Clones.clone(bribeMarketImplementation);

        IBribeMarket(bribeMarket).initialize(
            bribeVault,
            msg.sender,
            _protocol,
            _maxPeriods,
            _periodDuration
        );
        IBribeVault(bribeVault).grantDepositorRole(bribeMarket);

        emit BribeMarketCreated(bribeMarket);

        return bribeMarket;
    }

    /**
        @notice Set the bribe market implementation address
        @param  _implementation  address  Implementation address
     */
    function setBribeMarketImplementation(
        address _implementation
    ) external onlyOwner {
        _setBribeMarketImplementation(_implementation);

        emit BribeMarketImplementationUpdated(_implementation);
    }

    /**
        @notice Set the bribe vault address
        @param  _bribeVault  address  Bribe vault address
     */
    function setBribeVault(address _bribeVault) external onlyOwner {
        _setBribeVault(_bribeVault);

        emit BribeVaultUpdated(_bribeVault);
    }

    /**
        @notice Internal method to set the bribe market implementation address
        @param  _implementation  address  Implementation address
     */
    function _setBribeMarketImplementation(
        address _implementation
    ) internal isContract(_implementation) {
        bribeMarketImplementation = _implementation;
    }

    /**
        @notice Internal method to set the bribe vault address
        @param  _bribeVault  address  Bribe vault address
     */
    function _setBribeVault(
        address _bribeVault
    ) internal isContract(_bribeVault) {
        uint256 codeSize;
        assembly {
            codeSize := extcodesize(_bribeVault)
        }
        if (codeSize == 0) revert Errors.NotAContract();

        bribeVault = _bribeVault;
    }
}

contracts/BribeMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {IBribeVault} from "./interfaces/IBribeVault.sol";
import {Common} from "./libraries/Common.sol";
import {Errors} from "./libraries/Errors.sol";

contract BribeMarket is AccessControl, ReentrancyGuard {
    bytes32 public constant TEAM_ROLE = keccak256("TEAM_ROLE");

    uint256 public constant MAX_PERIODS = 10;
    uint256 public constant MAX_PERIOD_DURATION = 30 days;

    // Name (identifier) of the market, also used for rewardIdentifiers
    // Immutable after initialization
    string public PROTOCOL;

    // Address of the bribeVault
    // Immutable after initialization
    address public BRIBE_VAULT;

    // Maximum number of periods
    uint256 public maxPeriods;

    // Period duration
    uint256 public periodDuration;

    // Whitelisted bribe tokens
    address[] private _allWhitelistedTokens;

    // Blacklisted voters
    address[] private _allBlacklistedVoters;

    // Arbitrary bytes mapped to deadlines
    mapping(bytes32 => uint256) public proposalDeadlines;

    // Tracks whitelisted tokens
    mapping(address => uint256) public indexOfWhitelistedToken;

    // Tracks blacklisted voters
    mapping(address => uint256) public indexOfBlacklistedVoter;

    bool private _initialized;

    event Initialize(
        address bribeVault,
        address admin,
        string protocol,
        uint256 maxPeriods,
        uint256 periodDuration
    );
    event GrantTeamRole(address teamMember);
    event RevokeTeamRole(address teamMember);
    event SetProposals(bytes32[] proposals, uint256 indexed deadline);
    event SetProposalsById(
        uint256 indexed proposalIndex,
        bytes32[] proposals,
        uint256 indexed deadline
    );
    event SetProposalsByAddress(bytes32[] proposals, uint256 indexed deadline);
    event AddWhitelistedTokens(address[] tokens);
    event RemoveWhitelistedTokens(address[] tokens);
    event SetMaxPeriods(uint256 maxPeriods);
    event SetPeriodDuration(uint256 periodDuration);
    event AddBlacklistedVoters(address[] voters);
    event RemoveBlacklistedVoters(address[] voters);

    modifier onlyAuthorized() {
        if (
            !hasRole(DEFAULT_ADMIN_ROLE, msg.sender) &&
            !hasRole(TEAM_ROLE, msg.sender)
        ) revert Errors.NotAuthorized();
        _;
    }

    modifier onlyInitializer() {
        if (_initialized) revert Errors.AlreadyInitialized();
        _;
        _initialized = true;
    }

    /**
        @notice Initialize the contract
        @param  _bribeVault  Bribe vault address
        @param  _admin       Admin address
        @param  _protocol    Protocol name
        @param  _maxPeriods  Maximum number of periods
        @param  _periodDuration  Period duration
     */
    function initialize(
        address _bribeVault,
        address _admin,
        string calldata _protocol,
        uint256 _maxPeriods,
        uint256 _periodDuration
    ) external onlyInitializer {
        if (_bribeVault == address(0)) revert Errors.InvalidAddress();
        if (bytes(_protocol).length == 0) revert Errors.InvalidProtocol();
        if (_maxPeriods == 0 || _maxPeriods > MAX_PERIODS)
            revert Errors.InvalidMaxPeriod();
        if (_periodDuration == 0 || _periodDuration > MAX_PERIOD_DURATION)
            revert Errors.InvalidPeriodDuration();

        BRIBE_VAULT = _bribeVault;
        PROTOCOL = _protocol;
        maxPeriods = _maxPeriods;
        periodDuration = _periodDuration;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);

        emit Initialize(
            _bribeVault,
            _admin,
            _protocol,
            _maxPeriods,
            _periodDuration
        );
    }

    /**
        @notice Set multiple proposals with arbitrary bytes data as identifiers under the same deadline
        @param  _identifiers  bytes[]  identifiers
        @param  _deadline     uint256  Proposal deadline
     */
    function setProposals(
        bytes[] calldata _identifiers,
        uint256 _deadline
    ) external onlyAuthorized {
        uint256 identifiersLen = _identifiers.length;
        if (identifiersLen == 0) revert Errors.InvalidAddress();
        if (_deadline < block.timestamp) revert Errors.InvalidDeadline();

        bytes32[] memory proposalIds = new bytes32[](identifiersLen);

        uint256 i;
        do {
            if (_identifiers[i].length == 0) revert Errors.InvalidIdentifier();

            proposalIds[i] = keccak256(abi.encodePacked(_identifiers[i]));

            _setProposal(proposalIds[i], _deadline);

            ++i;
        } while (i < identifiersLen);

        emit SetProposals(proposalIds, _deadline);
    }

    /**
        @notice Set proposals based on the index of the proposal and the number of choices
        @param  _proposalIndex  uint256  Proposal index
        @param  _choiceCount    uint256  Number of choices to be voted for
        @param  _deadline       uint256  Proposal deadline
     */
    function setProposalsById(
        uint256 _proposalIndex,
        uint256 _choiceCount,
        uint256 _deadline
    ) external onlyAuthorized {
        if (_choiceCount == 0) revert Errors.InvalidChoiceCount();
        if (_deadline < block.timestamp) revert Errors.InvalidDeadline();

        bytes32[] memory proposalIds = new bytes32[](_choiceCount);

        uint256 i;
        do {
            proposalIds[i] = keccak256(abi.encodePacked(_proposalIndex, i));

            _setProposal(proposalIds[i], _deadline);

            ++i;
        } while (i < _choiceCount);

        emit SetProposalsById(_proposalIndex, proposalIds, _deadline);
    }

    /**
        @notice Set multiple proposals for many addresses under the same deadline
        @param  _addresses  address[]  addresses (eg. gauge addresses)
        @param  _deadline   uint256    Proposal deadline
     */
    function setProposalsByAddress(
        address[] calldata _addresses,
        uint256 _deadline
    ) external onlyAuthorized {
        uint256 addressesLen = _addresses.length;
        if (addressesLen == 0) revert Errors.InvalidAddress();
        if (_deadline < block.timestamp) revert Errors.InvalidDeadline();

        bytes32[] memory proposalIds = new bytes32[](addressesLen);

        uint256 i;
        do {
            if (_addresses[i] == address(0)) revert Errors.InvalidAddress();

            proposalIds[i] = keccak256(abi.encodePacked(_addresses[i]));

            _setProposal(proposalIds[i], _deadline);

            ++i;
        } while (i < addressesLen);

        emit SetProposalsByAddress(proposalIds, _deadline);
    }

    /**
        @notice Grant the team role to an address
        @param  _teamMember  address  Address to grant the teamMember role
     */
    function grantTeamRole(
        address _teamMember
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_teamMember == address(0)) revert Errors.InvalidAddress();
        _grantRole(TEAM_ROLE, _teamMember);

        emit GrantTeamRole(_teamMember);
    }

    /**
        @notice Revoke the team role from an address
        @param  _teamMember  address  Address to revoke the teamMember role
     */
    function revokeTeamRole(
        address _teamMember
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (!hasRole(TEAM_ROLE, _teamMember)) revert Errors.NotTeamMember();
        _revokeRole(TEAM_ROLE, _teamMember);

        emit RevokeTeamRole(_teamMember);
    }

    /**
        @notice Set maximum periods for submitting bribes ahead of time
        @param  _periods  uint256  Maximum periods
     */
    function setMaxPeriods(
        uint256 _periods
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_periods == 0 || _periods > MAX_PERIODS)
            revert Errors.InvalidMaxPeriod();

        maxPeriods = _periods;

        emit SetMaxPeriods(_periods);
    }

    /**
        @notice Set period duration per voting round
        @param  _periodDuration  uint256  Period duration
     */
    function setPeriodDuration(
        uint256 _periodDuration
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_periodDuration == 0 || _periodDuration > MAX_PERIOD_DURATION)
            revert Errors.InvalidPeriodDuration();

        periodDuration = _periodDuration;

        emit SetPeriodDuration(_periodDuration);
    }

    /**
        @notice Add whitelisted tokens
        @param  _tokens  address[]  Tokens to add to whitelist
     */
    function addWhitelistedTokens(
        address[] calldata _tokens
    ) external onlyAuthorized {
        uint256 tLen = _tokens.length;
        for (uint256 i; i < tLen; ) {
            if (_tokens[i] == address(0)) revert Errors.InvalidAddress();
            if (_tokens[i] == BRIBE_VAULT)
                revert Errors.NoWhitelistBribeVault();
            if (isWhitelistedToken(_tokens[i]))
                revert Errors.TokenWhitelisted();

            // Perform creation op for the unordered key set
            _allWhitelistedTokens.push(_tokens[i]);
            indexOfWhitelistedToken[_tokens[i]] =
                _allWhitelistedTokens.length -
                1;

            unchecked {
                ++i;
            }
        }

        emit AddWhitelistedTokens(_tokens);
    }

    /**
        @notice Remove whitelisted tokens
        @param  _tokens  address[]  Tokens to remove from whitelist
     */
    function removeWhitelistedTokens(
        address[] calldata _tokens
    ) external onlyAuthorized {
        uint256 tLen = _tokens.length;
        for (uint256 i; i < tLen; ) {
            if (!isWhitelistedToken(_tokens[i]))
                revert Errors.TokenNotWhitelisted();

            // Perform deletion op for the unordered key set
            // by swapping the affected row to the end/tail of the list
            uint256 index = indexOfWhitelistedToken[_tokens[i]];
            address tail = _allWhitelistedTokens[
                _allWhitelistedTokens.length - 1
            ];

            _allWhitelistedTokens[index] = tail;
            indexOfWhitelistedToken[tail] = index;

            delete indexOfWhitelistedToken[_tokens[i]];
            _allWhitelistedTokens.pop();

            unchecked {
                ++i;
            }
        }

        emit RemoveWhitelistedTokens(_tokens);
    }

    /**
        @notice Add blacklisted voters
        @param  _voters  address[]  Voters to add to blacklist
     */
    function addBlacklistedVoters(
        address[] calldata _voters
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 vLen = _voters.length;
        for (uint256 i; i < vLen; ) {
            if (_voters[i] == address(0)) revert Errors.InvalidAddress();
            if (isBlacklistedVoter(_voters[i]))
                revert Errors.VoterBlacklisted();

            _allBlacklistedVoters.push(_voters[i]);
            indexOfBlacklistedVoter[_voters[i]] =
                _allBlacklistedVoters.length -
                1;

            unchecked {
                ++i;
            }
        }

        emit AddBlacklistedVoters(_voters);
    }

    /**
        @notice Remove blacklisted voters
        @param  _voters  address[]  Voters to remove from blacklist
     */
    function removeBlacklistedVoters(
        address[] calldata _voters
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 vLen = _voters.length;
        for (uint256 i; i < vLen; ) {
            if (!isBlacklistedVoter(_voters[i]))
                revert Errors.VoterNotBlacklisted();

            // Perform deletion op for the unordered key set
            // by swapping the affected row to the end/tail of the list
            uint256 index = indexOfBlacklistedVoter[_voters[i]];
            address tail = _allBlacklistedVoters[
                _allBlacklistedVoters.length - 1
            ];

            _allBlacklistedVoters[index] = tail;
            indexOfBlacklistedVoter[tail] = index;

            delete indexOfBlacklistedVoter[_voters[i]];
            _allBlacklistedVoters.pop();

            unchecked {
                ++i;
            }
        }

        emit RemoveBlacklistedVoters(_voters);
    }

    /**
        @notice Deposit bribe for a proposal (ERC20 tokens only)
        @param  _proposal          bytes32  Proposal
        @param  _token             address  Token
        @param  _amount            uint256  Token amount
        @param  _maxTokensPerVote  uint256  Max amount of token per vote
        @param  _periods           uint256  Number of periods the bribe will be valid
     */
    function depositBribe(
        bytes32 _proposal,
        address _token,
        uint256 _amount,
        uint256 _maxTokensPerVote,
        uint256 _periods
    ) external nonReentrant {
        _depositBribe(
            _proposal,
            _token,
            _amount,
            _maxTokensPerVote,
            _periods,
            0,
            ""
        );
    }

    /**
        @notice Deposit bribe for a proposal (ERC20 tokens only) using permit
        @param  _proposal          bytes32  Proposal
        @param  _token             address  Token
        @param  _amount            uint256  Token amount
        @param  _maxTokensPerVote  uint256  Max amount of token per vote
        @param  _periods           uint256  Number of periods the bribe will be valid
        @param  _permitDeadline    uint256  Deadline for permit signature
        @param  _signature         bytes    Permit signature
     */
    function depositBribeWithPermit(
        bytes32 _proposal,
        address _token,
        uint256 _amount,
        uint256 _maxTokensPerVote,
        uint256 _periods,
        uint256 _permitDeadline,
        bytes memory _signature
    ) external nonReentrant {
        _depositBribe(
            _proposal,
            _token,
            _amount,
            _maxTokensPerVote,
            _periods,
            _permitDeadline,
            _signature
        );
    }

    /**
        @notice Return the list of currently whitelisted token addresses
     */
    function getWhitelistedTokens() external view returns (address[] memory) {
        return _allWhitelistedTokens;
    }

    /**
        @notice Return the list of currently blacklisted voter addresses
     */
    function getBlacklistedVoters() external view returns (address[] memory) {
        return _allBlacklistedVoters;
    }

    /**
        @notice Get bribe from BribeVault
        @param  _proposal          bytes32  Proposal
        @param  _proposalDeadline  uint256  Proposal deadline
        @param  _token             address  Token
        @return bribeToken         address  Token address
        @return bribeAmount        address  Token amount
     */
    function getBribe(
        bytes32 _proposal,
        uint256 _proposalDeadline,
        address _token
    ) external view returns (address bribeToken, uint256 bribeAmount) {
        (bribeToken, bribeAmount) = IBribeVault(BRIBE_VAULT).getBribe(
            keccak256(
                abi.encodePacked(
                    address(this),
                    _proposal,
                    _proposalDeadline,
                    _token
                )
            )
        );
    }

    /**
        @notice Return whether the specified token is whitelisted
        @param  _token  address Token address to be checked
     */
    function isWhitelistedToken(address _token) public view returns (bool) {
        if (_allWhitelistedTokens.length == 0) {
            return false;
        }

        return
            indexOfWhitelistedToken[_token] != 0 ||
            _allWhitelistedTokens[0] == _token;
    }

    /**
        @notice Return whether the specified address is blacklisted
        @param  _voter  address Voter address to be checked
     */
    function isBlacklistedVoter(address _voter) public view returns (bool) {
        if (_allBlacklistedVoters.length == 0) {
            return false;
        }

        return
            indexOfBlacklistedVoter[_voter] != 0 ||
            _allBlacklistedVoters[0] == _voter;
    }

    /**
        @notice Deposit bribe for a proposal (ERC20 tokens only) with optional permit parameters
        @param  _proposal          bytes32  Proposal
        @param  _token             address  Token
        @param  _amount            uint256  Token amount
        @param  _maxTokensPerVote  uint256  Max amount of token per vote
        @param  _periods           uint256  Number of periods the bribe will be valid
        @param  _permitDeadline    uint256  Deadline for permit signature
        @param  _signature         bytes    Permit signature
     */
    function _depositBribe(
        bytes32 _proposal,
        address _token,
        uint256 _amount,
        uint256 _maxTokensPerVote,
        uint256 _periods,
        uint256 _permitDeadline,
        bytes memory _signature
    ) internal {
        uint256 proposalDeadline = proposalDeadlines[_proposal];
        if (proposalDeadline < block.timestamp) revert Errors.DeadlinePassed();
        if (_periods == 0 || _periods > maxPeriods)
            revert Errors.InvalidPeriod();
        if (_token == address(0)) revert Errors.InvalidAddress();
        if (!isWhitelistedToken(_token)) revert Errors.TokenNotWhitelisted();
        if (_amount == 0) revert Errors.InvalidAmount();

        IBribeVault(BRIBE_VAULT).depositBribe(
            Common.DepositBribeParams({
                proposal: _proposal,
                token: _token,
                briber: msg.sender,
                amount: _amount,
                maxTokensPerVote: _maxTokensPerVote,
                periods: _periods,
                periodDuration: periodDuration,
                proposalDeadline: proposalDeadline,
                permitDeadline: _permitDeadline,
                signature: _signature
            })
        );
    }

    /**
        @notice Set a single proposal
        @param  _proposal  bytes32  Proposal
        @param  _deadline  uint256  Proposal deadline
     */
    function _setProposal(bytes32 _proposal, uint256 _deadline) internal {
        proposalDeadlines[_proposal] = _deadline;
    }
}

contracts/BribeVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {IPermit2} from "./interfaces/IPermit2.sol";
import {IRewardDistributor} from "./interfaces/IRewardDistributor.sol";
import {Common} from "./libraries/Common.sol";
import {Errors} from "./libraries/Errors.sol";

contract BribeVault is AccessControl {
    using SafeERC20 for IERC20;
    using SafeCast for uint256;

    struct Bribe {
        address token;
        uint256 amount;
    }

    struct Transfer {
        uint256 feeAmount;
        uint256 distributorAmountTransferred;
        uint256 distributorAmountReceived;
    }

    IPermit2 public constant PERMIT2 =
        IPermit2(0x000000000022D473030F116dDEE9F6B43aC78BA3);
    uint256 public constant FEE_DIVISOR = 1_000_000;
    uint256 public immutable FEE_MAX;
    bytes32 public constant DEPOSITOR_ROLE = keccak256("DEPOSITOR_ROLE");

    uint256 public fee; // 5000 = 0.5%
    address public feeRecipient; // Protocol treasury
    address public distributor; // RewardDistributor contract

    // Bribe identifiers mapped to Bribe structs
    // A bribe identifier is composed of different info (e.g. protocol, voting round, etc.)
    mapping(bytes32 => Bribe) public bribes;

    // Protocol-specific reward identifiers mapped to bribe identifiers
    // Allows us to group bribes by reward tokens (one token may be used across many bribes)
    mapping(bytes32 => bytes32[]) public rewardToBribes;

    // Tracks the reward transfer amounts
    mapping(bytes32 => Transfer) public rewardTransfers;

    // Voter addresses mapped to addresses which will claim rewards on their behalf
    mapping(address => address) public rewardForwarding;

    event SetFee(uint256 _fee);
    event SetFeeRecipient(address _feeRecipient);
    event SetDistributor(address _distributor);
    event DepositBribe(
        address indexed market,
        bytes32 indexed proposal,
        uint256 indexed deadline,
        address token,
        address briber,
        uint256 amount,
        uint256 totalAmount,
        uint256 maxTokensPerVote,
        uint256 periodIndex
    );
    event SetRewardForwarding(address from, address to);
    event TransferBribe(
        bytes32 indexed rewardIdentifier,
        address indexed token,
        uint256 feeAmount,
        uint256 distributorAmount
    );
    event EmergencyWithdrawal(
        address indexed token,
        uint256 amount,
        address admin
    );

    /**
        @param  _fee           uint256  Fee
        @param  _feeMax        unt256   Maximum fee
        @param  _feeRecipient  address  Fee recipient
        @param  _distributor   address  Reward distributor address
     */
    constructor(
        uint256 _fee,
        uint256 _feeMax,
        address _feeRecipient,
        address _distributor
    ) {
        // Max. fee shouldn't be >= 50%
        if (_feeMax >= FEE_DIVISOR / 2) revert Errors.InvalidMaxFee();
        if (_fee > _feeMax) revert Errors.InvalidFee();
        if (_feeRecipient == address(0)) revert Errors.InvalidFeeRecipient();
        if (_distributor == address(0)) revert Errors.InvalidDistributor();

        FEE_MAX = _feeMax;
        fee = _fee;
        feeRecipient = _feeRecipient;
        distributor = _distributor;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
        @notice Grant the depositor role to an address
        @param  depositor  address  Address to grant the depositor role
     */
    function grantDepositorRole(
        address depositor
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (depositor == address(0)) revert Errors.InvalidAddress();

        _grantRole(DEPOSITOR_ROLE, depositor);
    }

    /**
        @notice Revoke the depositor role from an address
        @param  depositor  address  Address to revoke the depositor role
     */
    function revokeDepositorRole(
        address depositor
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        // Keeping this redundant check to avoid emitting an event if no role is revoked
        if (!hasRole(DEPOSITOR_ROLE, depositor)) revert Errors.NotDepositor();

        _revokeRole(DEPOSITOR_ROLE, depositor);
    }

    /**
        @notice Set the fee collected by the protocol
        @param  _fee  uint256  Fee
     */
    function setFee(uint256 _fee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_fee > FEE_MAX) revert Errors.InvalidFee();

        fee = _fee;

        emit SetFee(_fee);
    }

    /**
        @notice Set the protocol address where fees will be transferred
        @param  _feeRecipient  address  Fee recipient
     */
    function setFeeRecipient(
        address _feeRecipient
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_feeRecipient == address(0)) revert Errors.InvalidFeeRecipient();

        feeRecipient = _feeRecipient;

        emit SetFeeRecipient(_feeRecipient);
    }

    /**
        @notice Set the RewardDistributor contract address
        @param  _distributor  address  Distributor
     */
    function setDistributor(
        address _distributor
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_distributor == address(0)) revert Errors.InvalidDistributor();

        distributor = _distributor;

        emit SetDistributor(_distributor);
    }

    /**
        @notice Generate the BribeVault identifier based on a scheme
        @param  _market           address  Market that originated the bribe
        @param  _proposal         bytes32  Proposal
        @param  _proposalDeadline uint256  Proposal deadline
        @param  _token            address  Token
        @return id                bytes32  BribeVault identifier
     */
    function generateBribeVaultIdentifier(
        address _market,
        bytes32 _proposal,
        uint256 _proposalDeadline,
        address _token
    ) public pure returns (bytes32 id) {
        id = keccak256(
            abi.encodePacked(_market, _proposal, _proposalDeadline, _token)
        );
    }

    /**
        @notice Generate the reward identifier based on a scheme
        @param  _market            address  Market that originated the reward
        @param  _proposalDeadline  uint256  Proposal deadline
        @param  _token             address  Token
        @return id                 bytes32  Reward identifier
     */
    function generateRewardIdentifier(
        address _market,
        address _token,
        uint256 _proposalDeadline
    ) public pure returns (bytes32 id) {
        id = keccak256(abi.encodePacked(_market, _proposalDeadline, _token));
    }

    /**
        @notice Get bribe information based on the specified identifier
        @param  bribeIdentifier  bytes32  The specified bribe identifier
     */
    function getBribe(
        bytes32 bribeIdentifier
    ) external view returns (address token, uint256 amount) {
        Bribe memory b = bribes[bribeIdentifier];
        return (b.token, b.amount);
    }

    /**
        @notice Return the list of bribe identifiers tied to the specified reward identifier
     */
    function getBribeIdentifiersByRewardIdentifier(
        bytes32 rewardIdentifier
    ) external view returns (bytes32[] memory) {
        return rewardToBribes[rewardIdentifier];
    }

    /**
        @notice Use permit to perform token transfer
        @param  _dp  DepositBribeParams  Deposit data
     */
    function _transferWithPermit(
        Common.DepositBribeParams calldata _dp
    ) internal {
        // Get current nonce
        (, , uint48 nonce) = PERMIT2.allowance(
            _dp.briber,
            _dp.token,
            address(this)
        );

        // Approve the transfer
        PERMIT2.permit(
            _dp.briber,
            IPermit2.PermitSingle({
                details: IPermit2.PermitDetails({
                    token: _dp.token,
                    amount: _dp.amount.toUint160(),
                    expiration: 2 ** 48 - 1,
                    nonce: nonce
                }),
                spender: address(this),
                sigDeadline: _dp.permitDeadline
            }),
            _dp.signature
        );

        // Transfer the tokens
        PERMIT2.transferFrom(
            _dp.briber,
            address(this),
            _dp.amount.toUint160(),
            _dp.token
        );
    }

    /**
        @notice Deposit bribe (ERC20 only)
        @param  _dp  DepositBribeParams  Deposit data
     */
    function depositBribe(
        Common.DepositBribeParams calldata _dp
    ) external onlyRole(DEPOSITOR_ROLE) {
        if (_dp.token == address(0)) revert Errors.InvalidToken();
        if (_dp.amount == 0) revert Errors.InvalidAmount();
        if (_dp.briber == address(0)) revert Errors.InvalidBriber();

        // Store the balance before transfer to calculate post-fee amount
        uint256 balanceBeforeTransfer = IERC20(_dp.token).balanceOf(
            address(this)
        );

        // Transfer the tokens to this contract
        // Uses permit2 if signature is provided
        if (_dp.signature.length > 0) {
            _transferWithPermit(_dp);
        } else {
            IERC20(_dp.token).safeTransferFrom(
                _dp.briber,
                address(this),
                _dp.amount
            );
        }

        // The difference between balances before and after the transfer is the actual bribe amount
        uint256 totalBribeAmount = IERC20(_dp.token).balanceOf(address(this)) -
            balanceBeforeTransfer;

        // Declare bribe data
        bytes32 bribeIdentifier;
        uint256 bribeAmount;
        uint256 deadline;

        // Loop through the specified periods
        uint256 i;
        do {
            deadline = _dp.proposalDeadline + (i * _dp.periodDuration);

            // Generate the bribe identifier
            bribeIdentifier = generateBribeVaultIdentifier(
                msg.sender,
                _dp.proposal,
                deadline,
                _dp.token
            );

            Bribe storage b = bribes[bribeIdentifier];

            // Increase bribe amount and add round error to first period
            bribeAmount =
                (totalBribeAmount / _dp.periods) +
                (i == 0 ? totalBribeAmount % _dp.periods : 0);

            b.amount += bribeAmount;

            // Only set the token address and update the reward-to-bribe mapping if not yet set
            if (b.token == address(0)) {
                b.token = _dp.token;
                rewardToBribes[
                    generateRewardIdentifier(msg.sender, _dp.token, deadline)
                ].push(bribeIdentifier);
            }

            emit DepositBribe(
                msg.sender,
                _dp.proposal,
                deadline,
                _dp.token,
                _dp.briber,
                bribeAmount,
                b.amount,
                _dp.maxTokensPerVote,
                i
            );

            unchecked {
                ++i;
            }
        } while (i < _dp.periods);
    }

    /**
        @notice Voters can opt in or out of reward-forwarding
        @notice Opt-in: A voter sets another address to forward rewards to
        @notice Opt-out: A voter sets their own address or the zero address
        @param  to  address  Account that rewards will be sent to
     */
    function setRewardForwarding(address to) external {
        rewardForwarding[msg.sender] = to;

        emit SetRewardForwarding(msg.sender, to);
    }

    /**
        @notice Calculate transfer amounts
        @param  rewardIdentifier   bytes32  Unique identifier related to reward
        @return feeAmount          uint256  Amount sent to the protocol treasury
        @return distributorAmount  uint256  Amount sent to the RewardDistributor
     */
    function calculateTransferAmounts(
        bytes32 rewardIdentifier
    ) private view returns (uint256 feeAmount, uint256 distributorAmount) {
        bytes32[] memory bribeIdentifiers = rewardToBribes[rewardIdentifier];
        uint256 totalAmount;
        uint256 bLen = bribeIdentifiers.length;

        for (uint256 i; i < bLen; ) {
            totalAmount += bribes[bribeIdentifiers[i]].amount;

            unchecked {
                ++i;
            }
        }

        feeAmount = (totalAmount * fee) / FEE_DIVISOR;
        distributorAmount = totalAmount - feeAmount;
    }

    /**
        @notice Transfer fees to fee recipient and bribes to distributor and update rewards metadata
        @param  rewardIdentifiers  bytes32[]  List of rewardIdentifiers
     */
    function transferBribes(
        bytes32[] calldata rewardIdentifiers
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 rLen = rewardIdentifiers.length;

        if (rLen == 0) revert Errors.InvalidArray();

        // Transfer the bribe funds to fee recipient and reward distributor
        for (uint256 i; i < rLen; ) {
            bytes32 rewardIdentifier = rewardIdentifiers[i];
            if (rewardToBribes[rewardIdentifier].length == 0)
                revert Errors.InvalidRewardIdentifier();

            Transfer storage r = rewardTransfers[rewardIdentifier];

            address token = bribes[rewardToBribes[rewardIdentifier][0]].token;
            (
                uint256 feeAmount,
                uint256 distributorAmount
            ) = calculateTransferAmounts(rewardIdentifier);

            if (r.distributorAmountTransferred == distributorAmount)
                revert Errors.BribeAlreadyTransferred();

            uint256 feeToTransfer = feeAmount - r.feeAmount;
            uint256 distributorAmountToTransfer = distributorAmount -
                r.distributorAmountTransferred;

            // Set the beforeFees field to prevent duplicate transfers
            r.feeAmount = feeAmount;
            r.distributorAmountTransferred = distributorAmount;

            IERC20 t = IERC20(token);

            // Necessary to calculate the after-fee amount received by the distributor
            uint256 distributorBalance = t.balanceOf(distributor);

            t.safeTransfer(feeRecipient, feeToTransfer);
            t.safeTransfer(distributor, distributorAmountToTransfer);

            // Set the "after fees" field to record actual amount received by the distributor
            uint256 finalAmountReceived = t.balanceOf(distributor) -
                distributorBalance;
            r.distributorAmountReceived += finalAmountReceived;

            emit TransferBribe(
                rewardIdentifier,
                token,
                feeAmount,
                finalAmountReceived
            );

            unchecked {
                ++i;
            }
        }
    }

    /**
        @notice Withdraw ERC20 tokens to the admin address
        @param  token   address  Token address
        @param  amount  uint256  Token amount
     */
    function emergencyWithdraw(
        address token,
        uint256 amount
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (token == address(0)) revert Errors.InvalidToken();
        if (amount == 0) revert Errors.InvalidAmount();

        IERC20(token).safeTransfer(msg.sender, amount);

        emit EmergencyWithdrawal(token, amount, msg.sender);
    }
}

