contracts/libraries/Common.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

library Common {
    /**
     * @param identifier  bytes32  Identifier of the distribution
     * @param token       address  Address of the token to distribute
     * @param merkleRoot  bytes32  Merkle root of the distribution
     * @param proof       bytes32  Proof of the distribution
     */
    struct Distribution {
        bytes32 identifier;
        address token;
        bytes32 merkleRoot;
        bytes32 proof;
    }

    /**
     * @param proposal          bytes32  Proposal to bribe
     * @param token             address  Token to bribe with
     * @param briber            address  Address of the briber
     * @param amount            uint256  Amount of tokens to bribe with
     * @param maxTokensPerVote  uint256  Maximum amount of tokens to use per vote
     * @param periods           uint256  Number of periods to bribe for
     * @param periodDuration    uint256  Duration of each period
     * @param proposalDeadline  uint256  Deadline for the proposal
     * @param permitDeadline    uint256  Deadline for the permit2 signature
     * @param signature         bytes    Permit2 signature
     */
    struct DepositBribeParams {
        bytes32 proposal;
        address token;
        address briber;
        uint256 amount;
        uint256 maxTokensPerVote;
        uint256 periods;
        uint256 periodDuration;
        uint256 proposalDeadline;
        uint256 permitDeadline;
        bytes signature;
    }

    /**
     * @param rwIdentifier      bytes32    Identifier for claiming reward
     * @param fromToken         address    Address of token to swap from
     * @param toToken           address    Address of token to swap to
     * @param fromAmount        uint256    Amount of fromToken to swap
     * @param toAmount          uint256    Amount of toToken to receive
     * @param deadline          uint256    Timestamp until which swap may be fulfilled
     * @param callees           address[]  Array of addresses to call (DEX addresses)
     * @param callLengths       uint256[]  Index of the beginning of each call in exchangeData
     * @param values            uint256[]  Array of encoded values for each call in exchangeData
     * @param exchangeData      bytes      Calldata to execute on callees
     * @param rwMerkleProof     bytes32[]  Merkle proof for the reward claim
     */
    struct ClaimAndSwapData {
        bytes32 rwIdentifier;
        address fromToken;
        address toToken;
        uint256 fromAmount;
        uint256 toAmount;
        uint256 deadline;
        address[] callees;
        uint256[] callLengths;
        uint256[] values;
        bytes exchangeData;
        bytes32[] rwMerkleProof;
    }

    /**
     * @param identifier   bytes32    Identifier for claiming reward
     * @param account      address    Address of the account to claim for
     * @param amount       uint256    Amount of tokens to claim
     * @param merkleProof  bytes32[]  Merkle proof for the reward claim
     */
    struct Claim {
        bytes32 identifier;
        address account;
        uint256 amount;
        bytes32[] merkleProof;
    }
}

contracts/libraries/Errors.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

library Errors {
    /**
     * @notice max period 0 or greater than MAX_PERIODS
     */
    error InvalidMaxPeriod();

    /**
     * @notice period duration 0 or greater than MAX_PERIOD_DURATION
     */
    error InvalidPeriodDuration();

    /**
     * @notice address provided is not a contract
     */
    error NotAContract();

    /**
     * @notice not authorized
     */
    error NotAuthorized();

    /**
     * @notice contract already initialized
     */
    error AlreadyInitialized();

    /**
     * @notice address(0)
     */
    error InvalidAddress();

    /**
     * @notice empty bytes identifier
     */
    error InvalidIdentifier();

    /**
     * @notice invalid protocol name
     */
    error InvalidProtocol();

    /**
     * @notice invalid number of choices
     */
    error InvalidChoiceCount();

    /**
     * @notice invalid input amount
     */
    error InvalidAmount();

    /**
     * @notice not team member
     */
    error NotTeamMember();

    /**
     * @notice cannot whitelist BRIBE_VAULT
     */
    error NoWhitelistBribeVault();

    /**
     * @notice token already whitelisted
     */
    error TokenWhitelisted();

    /**
     * @notice token not whitelisted
     */
    error TokenNotWhitelisted();

    /**
     * @notice voter already blacklisted
     */
    error VoterBlacklisted();

    /**
     * @notice voter not blacklisted
     */
    error VoterNotBlacklisted();

    /**
     * @notice deadline has passed
     */
    error DeadlinePassed();

    /**
     * @notice invalid period
     */
    error InvalidPeriod();

    /**
     * @notice invalid deadline
     */
    error InvalidDeadline();

    /**
     * @notice invalid max fee
     */
    error InvalidMaxFee();

    /**
     * @notice invalid fee
     */
    error InvalidFee();

    /**
     * @notice invalid fee recipient
     */
    error InvalidFeeRecipient();

    /**
     * @notice invalid distributor
     */
    error InvalidDistributor();

    /**
     * @notice invalid briber
     */
    error InvalidBriber();

    /**
     * @notice address does not have DEPOSITOR_ROLE
     */
    error NotDepositor();

    /**
     * @notice no array given
     */
    error InvalidArray();

    /**
     * @notice invalid reward identifier
     */
    error InvalidRewardIdentifier();

    /**
     * @notice bribe has already been transferred
     */
    error BribeAlreadyTransferred();

    /**
     * @notice distribution does not exist
     */
    error InvalidDistribution();

    /**
     * @notice invalid merkle root
     */
    error InvalidMerkleRoot();

    /**
     * @notice token is address(0)
     */
    error InvalidToken();

    /**
     * @notice claim does not exist
     */
    error InvalidClaim();

    /**
     * @notice reward is not yet active for claiming
     */
    error RewardInactive();

    /**
     * @notice timer duration is invalid
     */
    error InvalidTimerDuration();

    /**
     * @notice merkle proof is invalid
     */
    error InvalidProof();

    /**
     * @notice ETH transfer failed
     */
    error ETHTransferFailed();

    /**
     * @notice Invalid operator address
     */
    error InvalidOperator();

    /**
     * @notice call to TokenTransferProxy contract
     */
    error TokenTransferProxyCall();

    /**
     * @notice calling TransferFrom
     */
    error TransferFromCall();

    /**
     * @notice external call failed
     */
    error ExternalCallFailure();

    /**
     * @notice returned tokens too few
     */
    error InsufficientReturn();

    /**
     * @notice swapDeadline expired
     */
    error DeadlineBreach();

    /**
     * @notice expected tokens returned are 0
     */
    error ZeroExpectedReturns();

    /**
     * @notice arrays in SwapData.exchangeData have wrong lengths
     */
    error ExchangeDataArrayMismatch();
}

contracts/RewardDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {Common} from "./libraries/Common.sol";
import {Errors} from "./libraries/Errors.sol";

contract RewardDistributor is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    struct Reward {
        address token;
        bytes32 merkleRoot;
        bytes32 proof;
        uint256 activeAt;
    }

    struct Claim {
        bytes32 identifier;
        address account;
        uint256 amount;
        bytes32[] merkleProof;
    }

    uint256 public constant MINIMUM_ACTIVE_TIMER = 3 hours;

    // Maps each of the identifiers to its reward metadata
    mapping(bytes32 => Reward) public rewards;
    // Tracks the amount of claimed reward for the specified identifier+account
    mapping(bytes32 => mapping(address => uint256)) public claimed;
    // Used for calculating the timestamp on which rewards can be claimed after an update
    uint256 public activeTimerDuration;

    event RewardClaimed(
        bytes32 indexed identifier,
        address indexed token,
        address indexed account,
        uint256 amount
    );
    event RewardMetadataUpdated(
        bytes32 indexed identifier,
        address indexed token,
        bytes32 merkleRoot,
        bytes32 proof,
        uint256 activeAt
    );
    event SetActiveTimerDuration(uint256 duration);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setActiveTimerDuration(MINIMUM_ACTIVE_TIMER);
    }

    /**
        @notice Claim rewards based on the specified metadata
        @param  _claims  Claim[] List of claim metadata
     */
    function claim(
        Claim[] calldata _claims
    ) external nonReentrant whenNotPaused {
        uint256 cLen = _claims.length;

        if (cLen == 0) revert Errors.InvalidArray();

        for (uint256 i; i < cLen; ) {
            _claim(
                _claims[i].identifier,
                _claims[i].account,
                _claims[i].amount,
                _claims[i].merkleProof
            );

            unchecked {
                ++i;
            }
        }
    }

    /**
        @notice Update rewards metadata
        @param  _distributions  Distribution[] List of reward distribution details
     */
    function updateRewardsMetadata(
        Common.Distribution[] calldata _distributions
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 dLen = _distributions.length;

        if (dLen == 0) revert Errors.InvalidDistribution();

        uint256 activeAt = block.timestamp + activeTimerDuration;

        for (uint256 i; i < dLen; ) {
            // Update the metadata and start the timer until the rewards will be active/claimable
            Common.Distribution calldata distribution = _distributions[i];
            Reward storage reward = rewards[distribution.identifier];
            reward.merkleRoot = distribution.merkleRoot;
            reward.proof = distribution.proof;
            reward.activeAt = activeAt;

            // Should only be set once per identifier
            if (reward.token == address(0)) {
                reward.token = distribution.token;
            }

            emit RewardMetadataUpdated(
                distribution.identifier,
                distribution.token,
                distribution.merkleRoot,
                distribution.proof,
                activeAt
            );

            unchecked {
                ++i;
            }
        }
    }

    /** 
        @notice Set the contract's pause state (ie. before taking snapshot for the harvester)
        @dev    More efficient compared to setting the merkle proofs of all affected rewardIds to 0x
        @param  state  bool  Pause state
    */
    function setPauseState(bool state) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (state) {
            _pause();
        } else {
            _unpause();
        }
    }

    /**
        @notice Set the active timer duration
        @param  _duration  uint256  Timer duration
    */
    function changeActiveTimerDuration(
        uint256 _duration
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _setActiveTimerDuration(_duration);
    }

    /**
        @notice Claim a reward
        @param  _identifier   bytes32    Merkle identifier
        @param  _account      address    Eligible user account
        @param  _amount       uint256    Reward amount
        @param  _merkleProof  bytes32[]  Merkle proof
     */
    function _claim(
        bytes32 _identifier,
        address _account,
        uint256 _amount,
        bytes32[] calldata _merkleProof
    ) private {
        Reward memory reward = rewards[_identifier];

        if (reward.merkleRoot == 0) revert Errors.InvalidMerkleRoot();
        if (reward.activeAt > block.timestamp) revert Errors.RewardInactive();

        uint256 lifeTimeAmount = claimed[_identifier][_account] + _amount;

        // Verify the merkle proof
        if (
            !MerkleProof.verifyCalldata(
                _merkleProof,
                reward.merkleRoot,
                keccak256(abi.encodePacked(_account, lifeTimeAmount))
            )
        ) revert Errors.InvalidProof();

        // Update the claimed amount to the current total
        claimed[_identifier][_account] = lifeTimeAmount;

        address token = reward.token;

        IERC20(token).safeTransfer(_account, _amount);

        emit RewardClaimed(_identifier, token, _account, _amount);
    }

    /**
        @dev    Internal to set the active timer duration
        @param  _duration  uint256  Timer duration
     */
    function _setActiveTimerDuration(uint256 _duration) internal {
        if (_duration < MINIMUM_ACTIVE_TIMER)
            revert Errors.InvalidTimerDuration();

        activeTimerDuration = _duration;

        emit SetActiveTimerDuration(_duration);
    }
}

contracts/RewardHarvester.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {Errors} from "./libraries/Errors.sol";
import {Common} from "./libraries/Common.sol";

contract RewardHarvester is Ownable2Step {
    using SafeERC20 for IERC20;

    struct Reward {
        bytes32 merkleRoot;
        bytes32 hashedData;
        uint256 activeAt;
    }

    uint256 public constant FEE_BASIS = 1_000_000;
    uint256 public constant MAX_FEE = 100_000;
    uint256 public constant MINIMUM_ACTIVE_TIMER = 3 hours;

    // Maps members
    mapping(address => bool) public isMember;
    // Maps fees collected for each token
    mapping(address => uint256) public feesCollected;
    // Maps each of the identifier to its reward metadata
    mapping(address => Reward) public rewards;
    // Tracks the amount of claimed reward for the specified token and account
    mapping(address => mapping(address => uint256)) public claimed;
    // Harvest default token
    address public defaultToken;
    // Operator address
    address public operator;
    // Claimer address
    address public claimer;
    // Reward swapper address
    address public rewardSwapper;
    // Used for calculating the timestamp on which rewards can be claimed after an update
    uint256 public activeTimerDuration;

    //-----------------------//
    //        Events         //
    //-----------------------//
    event MemberJoined(address member);
    event MemberLeft(address member);
    event FeesCollected(address indexed token, uint256 amount);
    event BribeTransferred(address indexed token, uint256 totalAmount);
    event RewardClaimed(
        address indexed token,
        address indexed account,
        uint256 amount,
        uint256 postFeeAmount,
        address receiver
    );
    event RewardMetadataUpdated(
        address indexed token,
        bytes32 merkleRoot,
        bytes32 proof,
        uint256 activeAt
    );
    event DefaultTokenUpdated(address indexed token);
    event SetOperator(address indexed operator);
    event SetClaimer(address indexed claimer);
    event SetRewardSwapper(address indexed rewardSwapper);
    event SetActiveTimerDuration(uint256 duration);

    //-----------------------//
    //       Modifiers       //
    //-----------------------//
    /**
     * @notice Modifier to check caller is operator
     */
    modifier onlyOperatorOrOwner() {
        if (msg.sender != operator && msg.sender != owner())
            revert Errors.NotAuthorized();
        _;
    }

    /**
     * @notice Modifier to check caller is operator or reward swapper
     */
    modifier onlyOperatorOrRewardSwapper() {
        if (msg.sender != operator && msg.sender != rewardSwapper)
            revert Errors.NotAuthorized();
        _;
    }

    //-----------------------//
    //       Constructor     //
    //-----------------------//
    constructor(
        address _rewardSwapper,
        address _operator,
        address _defaultToken
    ) {
        _setDefaultToken(_defaultToken);
        _setOperator(_operator);
        _setRewardSwapper(_rewardSwapper);
        _setActiveTimerDuration(MINIMUM_ACTIVE_TIMER);
    }

    //-----------------------//
    //   External Functions  //
    //-----------------------//

    /**
        @notice Join the harvester to enable claiming rewards in default token
     */
    function join() external {
        isMember[msg.sender] = true;

        emit MemberJoined(msg.sender);
    }

    /**
        @notice Leave harvester
     */
    function leave() external {
        isMember[msg.sender] = false;

        emit MemberLeft(msg.sender);
    }

    /**
        @notice Claim rewards based on the specified metadata
        @dev    Can only be called by the claimer contract
        @param  _token        address    Token to claim rewards
        @param  _account      address    Account to claim rewards
        @param  _amount       uint256    Amount of rewards to claim
        @param  _merkleProof  bytes32[]  Merkle proof of the claim
        @param  _fee          uint256    Claim fee
        @param  _receiver     address    Receiver of the rewards
     */
    function claim(
        address _token,
        address _account,
        uint256 _amount,
        bytes32[] calldata _merkleProof,
        uint256 _fee,
        address _receiver
    ) external {
        if (msg.sender != claimer) revert Errors.NotAuthorized();
        if (_account == address(0)) revert Errors.InvalidClaim();
        if (_amount == 0) revert Errors.InvalidAmount();
        if (_fee > MAX_FEE) revert Errors.InvalidFee();

        // Calculate amount after any fees
        uint256 feeAmount = (_amount * _fee) / FEE_BASIS;
        uint256 postFeeAmount = _amount - feeAmount;
        feesCollected[_token] += feeAmount;

        Reward memory reward = rewards[_token];
        uint256 lifeTimeAmount = claimed[_token][_account] + _amount;

        if (reward.merkleRoot == 0) revert Errors.InvalidDistribution();
        if (reward.activeAt > block.timestamp) revert Errors.RewardInactive();

        // Verify the merkle proof
        if (
            !MerkleProof.verifyCalldata(
                _merkleProof,
                reward.merkleRoot,
                keccak256(abi.encodePacked(_account, lifeTimeAmount))
            )
        ) revert Errors.InvalidProof();

        // Update the claimed amount to the current total
        claimed[_token][_account] = lifeTimeAmount;

        IERC20(_token).safeTransfer(_receiver, postFeeAmount);

        emit RewardClaimed(_token, _account, _amount, postFeeAmount, _receiver);
    }

    /**
        @notice Deposit `defaultToken` to this contract
        @param  _amount  uint256  Amount of `defaultToken` to deposit
     */
    function depositReward(
        uint256 _amount
    ) external onlyOperatorOrRewardSwapper {
        if (_amount == 0) revert Errors.InvalidAmount();

        IERC20 token = IERC20(defaultToken);

        uint256 initialAmount = token.balanceOf(address(this));

        token.safeTransferFrom(msg.sender, address(this), _amount);

        emit BribeTransferred(
            defaultToken,
            token.balanceOf(address(this)) - initialAmount
        );
    }

    /**
        @notice Update rewards metadata
        @param  _token       address  Token to update rewards metadata
        @param  _merkleRoot  bytes32  Merkle root of the rewards
        @param  _hashedData  bytes32  Hashed data of the rewards
     */
    function updateRewardsMetadata(
        address _token,
        bytes32 _merkleRoot,
        bytes32 _hashedData
    ) external onlyOperatorOrOwner {
        if (_token == address(0)) revert Errors.InvalidToken();
        if (_merkleRoot == 0) revert Errors.InvalidMerkleRoot();

        // Update the metadata and start the timer until the rewards will be active/claimable
        uint256 activeAt = block.timestamp + activeTimerDuration;
        Reward storage reward = rewards[_token];
        reward.merkleRoot = _merkleRoot;
        reward.hashedData = _hashedData;
        reward.activeAt = activeAt;

        emit RewardMetadataUpdated(_token, _merkleRoot, _hashedData, activeAt);
    }

    /**
        @notice Collect fees
        @param  _token  address  Token to collect fees
     */
    function collectFees(address _token) external onlyOwner {
        uint256 amount = feesCollected[_token];
        if (amount == 0) revert Errors.InvalidAmount();

        feesCollected[_token] = 0;
        IERC20(_token).safeTransfer(msg.sender, amount);

        emit FeesCollected(_token, amount);
    }

    /**
        @notice Change the operator
        @param  _operator  address  New operator address
     */
    function changeOperator(address _operator) external onlyOwner {
        _setOperator(_operator);
    }

    /**
        @notice Change the `defaultToken`
        @param  _newToken  address  New default token address
     */
    function changeDefaultToken(address _newToken) external onlyOwner {
        _setDefaultToken(_newToken);
    }

    /**
        @notice Change the RewardSwapper contract
        @param  _newSwapper  address  New reward swapper address
     */
    function changeRewardSwapper(address _newSwapper) external onlyOwner {
        _setRewardSwapper(_newSwapper);
    }

    /**
        @notice Change claimer address
        @param  _claimer  address  New claimer address
     */
    function changeClaimer(address _claimer) external onlyOwner {
        if (_claimer == address(0)) revert Errors.InvalidAddress();

        claimer = _claimer;

        emit SetClaimer(_claimer);
    }

    /**
        @notice Set the active timer duration
        @param  _duration  uint256  Timer duration
    */
    function changeActiveTimerDuration(uint256 _duration) external onlyOwner {
        _setActiveTimerDuration(_duration);
    }

    //-----------------------//
    //   Internal Functions  //
    //-----------------------//
    /**
        @dev    Internal to set the default token
        @param  _newToken  address  Token address
     */
    function _setDefaultToken(address _newToken) internal {
        if (_newToken == address(0)) revert Errors.InvalidToken();

        defaultToken = _newToken;

        emit DefaultTokenUpdated(_newToken);
    }

    /**
        @dev    Internal to set the RewardSwapper contract
        @param  _newSwapper  address  RewardSwapper address
     */
    function _setRewardSwapper(address _newSwapper) internal {
        if (_newSwapper == address(0)) revert Errors.InvalidAddress();

        rewardSwapper = _newSwapper;

        emit SetRewardSwapper(_newSwapper);
    }

    /**
        @dev    Internal to set the operator
        @param  _operator  address  Token address
     */
    function _setOperator(address _operator) internal {
        if (_operator == address(0)) revert Errors.InvalidOperator();

        operator = _operator;

        emit SetOperator(_operator);
    }

    /**
        @dev    Internal to set the active timer duration
        @param  _duration  uint256  Timer duration
     */
    function _setActiveTimerDuration(uint256 _duration) internal {
        if (_duration < MINIMUM_ACTIVE_TIMER)
            revert Errors.InvalidTimerDuration();

        activeTimerDuration = _duration;

        emit SetActiveTimerDuration(_duration);
    }
}

contracts/RewardHarvesterClaim.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {RewardHarvester} from "./RewardHarvester.sol";
import {Errors} from "./libraries/Errors.sol";

contract RewardHarvesterClaim is Ownable2Step, ReentrancyGuard {
    uint256 public constant MAX_FEE = 100_000;

    // Harvester fee
    // Only single fee tracking for this version, but can be expanded for varying fees by token later
    uint256 public fee;
    // Harvester contract
    RewardHarvester public immutable harvester;

    //-----------------------//
    //        Events         //
    //-----------------------//
    event SetFee(uint256 fee);

    //-----------------------//
    //       Constructor     //
    //-----------------------//
    constructor(address _harvester, uint256 _fee) {
        if (_harvester == address(0)) revert Errors.InvalidAddress();

        harvester = RewardHarvester(_harvester);

        _setFee(_fee);
    }

    //-----------------------//
    //   External Functions  //
    //-----------------------//

    /**
        @notice Claim rewards based on the specified metadata
        @dev    Currently only perform direct claiming for this version
        @param  _token        address    Token to claim rewards
        @param  _account      address    Account to claim rewards
        @param  _amount       uint256    Amount of rewards to claim
        @param  _merkleProof  bytes32[]  Merkle proof of the claim
     */
    function claim(
        address _token,
        address _account,
        uint256 _amount,
        bytes32[] calldata _merkleProof
    ) external nonReentrant {
        // Receiver is currently set to the user itself for this version of claimer
        // but can be directed to the claimer first in the future for additional actions (ie. swaps, locks)
        harvester.claim(_token, _account, _amount, _merkleProof, fee, _account);
    }

    /**
        @notice Change fee
        @param  _newFee  uint256  New fee to set
     */
    function changeFee(uint256 _newFee) external onlyOwner {
        _setFee(_newFee);
    }

    //-----------------------//
    //   Internal Functions  //
    //-----------------------//
    /**
        @dev    Internal to set the fee
        @param  _newFee  uint256  Token address
     */
    function _setFee(uint256 _newFee) internal {
        if (_newFee > MAX_FEE) revert Errors.InvalidFee();

        fee = _newFee;

        emit SetFee(_newFee);
    }
}

contracts/RewardSwapper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {IRewardDistributor} from "./interfaces/IRewardDistributor.sol";
import {IRewardHarvester} from "./interfaces/IRewardHarvester.sol";
import {Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Errors} from "./libraries/Errors.sol";
import {Common} from "./libraries/Common.sol";

contract RewardSwapper is Ownable2Step {
    using SafeERC20 for IERC20;

    IRewardDistributor public rewardDistributor;
    IRewardHarvester public rewardHarvester;

    // Operator address
    address public operator;

    //-----------------------//
    //        Events         //
    //-----------------------//
    event SetOperator(address indexed operator);
    event SetRewardHarvester(address indexed rewardHarvester);
    event SetRewardDistributor(address indexed rewardDistributor);
    event BribeTransferred(address indexed token, uint256 totalAmount);

    /**
     * @notice Modifier to check caller is operator
     */
    modifier onlyOperator() {
        if (msg.sender != operator) revert Errors.NotAuthorized();
        _;
    }

    //-----------------------//
    //       Constructor     //
    //-----------------------//
    constructor(
        address _rewardDistributor,
        address _rewardHarvester,
        address _operator
    ) {
        _setRewardDistributor(_rewardDistributor);
        _setRewardHarvester(_rewardHarvester);
        _setOperator(_operator);
    }

    //-----------------------//
    //   External Functions  //
    //-----------------------//

    /**
     * @notice Executes swaps via DEX
     * @param  _claimSwapData  Common.ClaimAndSwapData[]  The data for the claims+swaps
     */
    function claimSwapAndDepositReward(
        Common.ClaimAndSwapData[] calldata _claimSwapData
    ) external onlyOperator {
        uint256 cLen = _claimSwapData.length;

        if (cLen == 0) revert Errors.InvalidArray();

        IERC20 defaultToken = IERC20(rewardHarvester.defaultToken());

        uint256 initalAmount = defaultToken.balanceOf(address(this));

        Common.Claim[] memory claimData = new Common.Claim[](
            _claimSwapData.length
        );

        // Claim rewards
        for (uint256 i; i < cLen; ) {
            claimData[i].identifier = _claimSwapData[i].rwIdentifier;
            claimData[i].account = address(this);
            claimData[i].amount = _claimSwapData[i].fromAmount;
            claimData[i].merkleProof = _claimSwapData[i].rwMerkleProof;

            unchecked {
                ++i;
            }
        }

        rewardDistributor.claim(claimData);

        // Swap reward tokens to default token
        for (uint256 i; i < cLen; ) {
            _swap(_claimSwapData[i]);

            unchecked {
                ++i;
            }
        }

        uint256 amountClaimed = defaultToken.balanceOf(address(this)) -
            initalAmount;

        // Approve reward harvester if needed
        if (
            defaultToken.allowance(address(this), address(rewardHarvester)) <
            amountClaimed
        ) {
            defaultToken.safeApprove(
                address(rewardHarvester),
                type(uint256).max
            );
        }

        // Deposit reward
        rewardHarvester.depositReward(amountClaimed);

        emit BribeTransferred(address(defaultToken), amountClaimed);
    }

    /**
        @notice Change the operator
        @param  _operator  address  New operator address
     */
    function changeOperator(address _operator) external onlyOwner {
        _setOperator(_operator);
    }

    /**
        @notice Change the reward harvester address
        @param  _harvester  address  New harvester address
     */
    function changeRewardHarvester(address _harvester) external onlyOwner {
        _setRewardHarvester(_harvester);
    }

    /**
        @notice Change the reward distributor address
        @param  _distributor  address  New distributor address
     */
    function changeRewardDistributor(address _distributor) external onlyOwner {
        _setRewardDistributor(_distributor);
    }

    //-----------------------//
    //   Internal Functions  //
    //-----------------------//

    /**
        @dev    Internal to set the operator
        @param  _operator  address  Operator address
     */
    function _setOperator(address _operator) internal {
        if (_operator == address(0)) revert Errors.InvalidAddress();

        operator = _operator;

        emit SetOperator(_operator);
    }

    /**
        @dev    Internal to set the reward harvester
        @param  _harvester  address  Reward Harvester address
     */
    function _setRewardHarvester(address _harvester) internal {
        if (_harvester == address(0)) revert Errors.InvalidAddress();

        rewardHarvester = IRewardHarvester(_harvester);

        emit SetRewardHarvester(_harvester);
    }

    /**
        @dev    Internal to set the reward distributor
        @param  _distributor  address  Distributor address
     */
    function _setRewardDistributor(address _distributor) internal {
        if (_distributor == address(0)) revert Errors.InvalidAddress();

        rewardDistributor = IRewardDistributor(_distributor);

        emit SetRewardDistributor(_distributor);
    }

    /**
     * @notice Executes a sequence of swaps via DEX
     * @param  _swapData       Common.SwapData  The data for the swaps
     * @return receivedAmount  uint256          The final amount of the toToken received
     */
    function _swap(
        Common.ClaimAndSwapData memory _swapData
    ) internal returns (uint256 receivedAmount) {
        if (
            !(_swapData.callees.length == _swapData.callLengths.length &&
                _swapData.callees.length == _swapData.values.length)
        ) {
            revert Errors.ExchangeDataArrayMismatch();
        }

        if (_swapData.deadline < block.timestamp) {
            revert Errors.DeadlineBreach();
        }

        if (_swapData.toAmount == 0) {
            revert Errors.ZeroExpectedReturns();
        }

        bytes memory exchangeData = _swapData.exchangeData;
        uint256 calleesLength = _swapData.callees.length;

        if (calleesLength == 0) revert Errors.InvalidArray();

        bytes4 transferFromSelector = IERC20.transferFrom.selector;
        uint256 initialAmount = IERC20(_swapData.toToken).balanceOf(
            address(this)
        );

        uint256 currentDataStartIndex = 0;
        for (uint256 i; i < calleesLength; ) {
            // Check if the call is a transferFrom call
            // protect caller from transferring more than `fromAmount`
            {
                bytes32 selector;
                assembly {
                    selector := mload(add(exchangeData, add(currentDataStartIndex, 32)))
                }
                if (bytes4(selector) == transferFromSelector) {
                    revert Errors.TransferFromCall();
                }
            }
            bool result = _externalCall(
                _swapData.callees[i], //destination
                _swapData.values[i], //value to send
                currentDataStartIndex, // start index of call data
                _swapData.callLengths[i], // length of calldata
                exchangeData // total calldata
            );
            if (!result) {
                revert Errors.ExternalCallFailure();
            }
            currentDataStartIndex += _swapData.callLengths[i];
            unchecked {
                ++i;
            }
        }

        receivedAmount = IERC20(_swapData.toToken).balanceOf(address(this));

        if ((receivedAmount - initialAmount) < _swapData.toAmount) {
            revert Errors.InsufficientReturn();
        }
    }

    /**
     * @dev Source take from GNOSIS MultiSigWallet
     * @dev https://github.com/gnosis/MultiSigWallet/blob/master/contracts/MultiSigWallet.sol
     */
    function _externalCall(
        address _destination,
        uint256 _value,
        uint256 _dataOffset,
        uint256 _dataLength,
        bytes memory _data
    ) internal returns (bool) {
        bool result;
        assembly {
            let x := mload(0x40) // "Allocate" memory for output
            // (0x40 is where "free memory" pointer is stored by convention)

            let d := add(_data, 32) // First 32 bytes are the padded length of data, so exclude that
            result := call(
                gas(),
                _destination,
                _value,
                add(d, _dataOffset),
                _dataLength, // Size of the input (in bytes) - this is what fixes the padding problem
                x,
                0 // Output is ignored, therefore the output size is zero
            )
        }
        return result;
    }
}

contracts/VaultOperator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {Common} from "./libraries/Common.sol";
import {IBribeVault} from "./interfaces/IBribeVault.sol";
import {IRewardDistributor} from "./interfaces/IRewardDistributor.sol";
import {Errors} from "./libraries/Errors.sol";

contract VaultOperator is Ownable2Step {
    IBribeVault public immutable bribeVault;
    IRewardDistributor public immutable rewardDistributor;
    address public operator;

    event SetOperator(address indexed operator);

    /**
        @param  _bribeVault         Address of the bribe vault
        @param  _rewardDistributor  Address of the reward distributor
     */
    constructor(address _bribeVault, address _rewardDistributor) {
        if (_bribeVault == address(0)) revert Errors.InvalidAddress();
        if (_rewardDistributor == address(0)) revert Errors.InvalidAddress();

        bribeVault = IBribeVault(_bribeVault);
        rewardDistributor = IRewardDistributor(_rewardDistributor);

        // Default to the deployer
        operator = msg.sender;
    }

    modifier onlyOperator() {
        if (msg.sender != operator) revert Errors.NotAuthorized();
        _;
    }

    /**
        @notice Set the operator
        @param  _operator  address  Operator address
     */
    function setOperator(address _operator) external onlyOwner {
        if (_operator == address(0)) revert Errors.InvalidAddress();

        operator = _operator;

        emit SetOperator(_operator);
    }

    /**
        @notice Redirect transferBribes call to the bribeVault for approved operator
        @param  rewardIdentifiers  bytes32[]  List of rewardIdentifiers
     */
    function transferBribes(
        bytes32[] calldata rewardIdentifiers
    ) external onlyOperator {
        bribeVault.transferBribes(rewardIdentifiers);
    }

    /**
        @notice Redirect updateRewardsMetadata call to the rewardDistributor for approved operator
        @param  distributions  Distribution[]  List of reward distribution details
     */
    function updateRewardsMetadata(
        Common.Distribution[] calldata distributions
    ) external onlyOperator {
        rewardDistributor.updateRewardsMetadata(distributions);
    }

    /**
        @notice Set pause state on the rewardDistributor for approved operator
        @param  state  bool  Pause state
     */
    function setPauseState(bool state) external onlyOperator {
        rewardDistributor.setPauseState(state);
    }
}

